import { useEffect, useState } from "react";
import Template from "../components/Template";
import swal from "sweetalert2";
import axios from "axios";
import config from "../config";

import "../css/sale.css"; // Make sure to import the CSS file

export default function Sale() {
  const [products, setProducts] = useState([]);
  const [billSale, setBillSale] = useState({});
  const [currentBill, setcurrentBills] = useState({});
  const [totalPrice, settotalPrice] = useState(0);

  useEffect(() => {
    fetchData();
    openBill();
    fetchBillSaleDetail();
  }, []);

  const fetchBillSaleDetail = async () => {
    try {
      const res = await axios.get(
        `${config.api_path}/billSale/currentBillinfo`,
        config.headers()
      );
      setcurrentBills(res.data.result);
      sumTotalPrice(res.data.result.billsaledetails);
    } catch (e) {
      showErrorAlert(e.message);
    }
  };

  const DeleteItem =(item)=>{
    swal.fire({
      title:"ยืนยันการลบ",
      text:"ต้องการลบรายงาน สินค้าใช่หรือไม่",
      icon:"question",
      showCancelButton:true,
      showConfirmButton:true
    }).then (async res=>{
      if(res.isConfirme){
        await axios.delete(config.api_path+'/billSale/deleteItem',item.id,config.headers()).then(res=>{
          if(res.data.message==='success'){
            swal.fire({
              title:"ลบรายงานสินค้า",
              text:"ลบรายงานสินค้าสำเร็จ"

            })
          }
        })
      }
    })
  }
  const openBill = async () => {
    try {
      const res = await axios.get(
        `${config.api_path}/billSale/openBill`,
        config.headers()
      );
      setBillSale(res.data.result);
    } catch (e) {
      showErrorAlert(e.message);
    }
  };

  const fetchData = async () => {
    try {
      const res = await axios.get(
        `${config.api_path}/product/listForSale`,
        config.headers()
      );
      if (res.data.message === "success") {
        setProducts(res.data.result);
      }
    } catch (e) {
      showErrorAlert(e.message);
    }
  };

  const Sale = async (item) => {
    try {
      const res = await axios.post(
        `${config.api_path}/billSale/sale`,
        item,
        config.headers()
      );
      if (res.data.message === "success") {
        fetchBillSaleDetail();
      }
    } catch (e) {
      showErrorAlert(e.message);
    }
  };

  const showErrorAlert = (message) => {
    swal.fire({
      title: "Error",
      text: message,
      icon: "error",
    });
  };

  const sumTotalPrice = (billsaledetails) => {
    let sum = 0;

    for (let i = 0; i < billsaledetails.length; i++) {
      const item = billsaledetails[i];
      const qty = parseInt(item.qty);
      const price = parseInt(item.price);

      sum += qty * price;
    }
    settotalPrice(sum);
  };

  return (
    <Template>
      <div className="card mt-4">
        <div className="card-header d-flex justify-content-between align-items-center bg-success text-white">
          <div className="h5 mb-0">ขายสินค้า</div>
          <div>
            <button className="btn btn-light me-2">
              <i className="fa fa-check me-2"></i>จบการขาย
            </button>
            <button className="btn btn-info me-2">
              <i className="fa fa-file me-2"></i>จบบิลวันนี้
            </button>
            <button className="btn btn-secondary">
              <i className="fa fa-file-alt me-2"></i>จบบิลล่าสุด
            </button>
          </div>
        </div>
        <div className="card-body">
          <div className="row mt-3">
            <div className="col-9">
              <div className="row">
                {products.length > 0 ? (
                  products.map((item) => (
                    <div
                      className="col-12 col-sm-6 col-md-4 col-lg-3 mb-4"
                      key={item.id}
                    >
                      <div className="card h-100 card-hover">
                        <img
                          className="card-img-top product-img"
                          src={`${config.api_path}/uploads/${item.productImages[0].imageName}`}
                          alt={item.name}
                        />
                        <div className="card-body">
                          <h5 className="card-title text-primary text-center">
                            {item.name}
                          </h5>
                          <p className="card-text text-center">
                            {parseInt(item.price).toLocaleString("th-TH")}
                            &nbsp;฿
                          </p>
                          <button
                            className="btn btn-primary w-100"
                            onClick={() => Sale(item)}
                          >
                            <i className="fa fa-shopping-cart"></i> ซื้อ
                          </button>
                        </div>
                      </div>
                    </div>
                  ))
                ) : (
                  <p className="text-center">No products available</p>
                )}
              </div>
            </div>

            <div className="col-3">
              <div className="text-end mb-4">
                <span className="bg-dark text-white h2 ps-5 pe-5 py-2 rounded">
                  {totalPrice.toLocaleString("th-TH")}&nbsp;฿
                </span>
              </div>
              {Object.keys(currentBill).length > 0 ? (
                currentBill.billsaledetails.map((item, index) => (
                  <div
                    key={index}
                    className="list-group-item text-end justify-content-between align-items-center card w-100 mb-2 pb-2 pt-2"
                  >
                    <div className="mt-2 text-primary h4 text-center">
                      {item.product.name}
                    </div>
                    <div className="mt-2 mb-2 text-center">
                      <span className="text-danger h5">{item.qty}</span>
                      <span>
                        &nbsp;x {parseInt(item.price).toLocaleString("th-TH")} ={" "}
                        {(item.qty * item.price).toLocaleString("th-TH")} ฿
                      </span>
                      <div className="mt-2 d-flex justify-content-center">
                        <button className="btn btn-primary me-2">
                          <i className="fa fa-pencil"></i>
                        </button>
                        <button
                          className="btn btn-danger"
                          onClick={() => DeleteItem(item)}
                        >
                          <i className="fa fa-trash"></i>
                        </button>
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <p className="text-center">No current bill details</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </Template>
  );
}
