import { useState, useEffect } from "react";
import swal from "sweetalert2";
import axios from "axios";
import config from "../config";
import { Link } from "react-router-dom";
import Modal from "../components/Modal";
import "../css/hover.css"; // Import the CSS file

export default function Sidebar() {
  const [memberName, setMemberName] = useState();
  const [packageName, setPackageName] = useState();
  const [Packages, setPackages] = useState([]);

  useEffect(() => {
    fetchData();
    fetchPackages();
  }, []);

  const fetchData = async () => {
    try {
      const res = await axios.get(
        config.api_path + "/member/info",
        config.headers()
      );
      if (res.data.message === "success") {
        setMemberName(res.data.result.name);
        setPackageName(res.data.result.package.name);
      }
    } catch (e) {
      swal.fire({
        title: "Error",
        text: e.message,
        icon: "error",
      });
    }
  };

  const fetchPackages = async () => {
    try {
      const res = await axios.get(
        config.api_path + "/package/list",
        config.headers()
      );

      if (res.data.results && res.data.results.length > 0) {
        setPackages(res.data.results);
      }
    } catch (e) {
      ShowError(e.message);
    }
  };

  const ShowError = (message) => {
    swal.fire({
      title: "Error",
      text: message,
      icon: "error",
    });
  };

  const renderButton = (item) => {
    if (packageName == item.name) {
      return (
        <button
          type="button"
          className="btn btn-primary disabled"
          data-bs-toggle="modal"
          data-bs-target="#modalUpgrade"
          disabled
        >
          เลือก
        </button>
      );
    } else {
    }
    return (
      <button
        type="button"
        className="btn btn-primary"
        data-bs-toggle="modal"
        data-bs-target="#modalUpgrade"
      >
        เลือก
      </button>
    );
  };
  return (
    <>
      <aside className="main-sidebar sidebar-dark-primary elevation-4">
        {/* Brand Logo */}
        <Link to="/home" className="brand-link">
          <img
            src="dist/img/AdminLTELogo.png"
            alt="AdminLTE Logo"
            className="brand-image img-circle elevation-3"
            style={{ opacity: ".8" }}
          />
          <span className="brand-text font-weight-light">
            POS ขายเห้ไรดีหว่ะ
          </span>
        </Link>
        {/* Sidebar */}
        <div className="sidebar">
          {/* Sidebar user panel (optional) */}
          <div className="user-panel mt-3 pb-3 mb-3 d-flex">
            <div className="image">
              <img
                src="dist/img/user2-160x160.jpg"
                className="img-circle elevation-2"
                alt="User Image"
              />
            </div>
            <div className="info text-light">
              <div>Name: {memberName}</div>
              <div>Package: {packageName}</div>
              <div>
                <a
                  onClick={fetchPackages}
                  data-bs-toggle="modal"
                  data-bs-target="#modalUpgrade"
                  className="btn btn-warning mt-2 icon-link icon-link-hover"
                  href="#"
                >
                  <i className="fa-sharp fa-solid fa-arrow-up me-2"></i>
                  {e}
                  Upgrade
                </a>
              </div>
            </div>
          </div>
          {/* SidebarSearch Form */}
          <div className="form-inline">
            <div className="input-group" data-widget="sidebar-search">
              <input
                className="form-control form-control-sidebar"
                type="search"
                placeholder="Search"
                aria-label="Search"
              />
              <div className="input-group-append">
                <button className="btn btn-sidebar">
                  <i className="fas fa-search fa-fw" />
                </button>
              </div>
            </div>
          </div>
          {/* Sidebar Menu */}
          <nav className="mt-2">
            <ul
              className="nav nav-pills nav-sidebar flex-column"
              data-widget="treeview"
              role="menu"
              data-accordion="false"
            >
              <li className="nav-item">
