import Template from "../components/Template";
import { useEffect, useState } from "react";
import swal from "sweetalert2";
import axios from "axios";
import config from "../config";
import Modal from "../components/Modal";

export default function ReportStock() {
  const [stocks, setStocks] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const res = await axios.get(config.api_path + "/stock/report", config.headers());
      if (res.data.message === "success") {
        setStocks(res.data.result || []);
      } else {
        ShowError("Failed to fetch stock data");
      }
    } catch (e) {
      ShowError(e.message);
    }
  };

  const ShowError = (message) => {
    swal.fire({
      title: "Error",
      text: message,
      icon: "error",
    });
  };

  return (
    <Template>
      <div className="card">
        <div className="card-header d-flex justify-content-between align-items-center bg-success text-white">
          <div className="h4 mb-0">รายงาน Stock</div>
        </div>
        <div className="card-body">
          <table className="table table-bordered table-striped table-hover mt-3">
            <thead className="table-white text-center">
              <tr>
                <td>Barcode</td>
                <td>รายการ</td>
                <td>รับเข้า</td>
                <td>ขายออก</td>
                <td>คงเหลือ</td>
              </tr>
            </thead>
            <tbody className="text-center">
              {stocks.map((item, index) => (
                <tr key={index}>
                  <td>{item.product.barcode}</td>
                  <td >{item.product.name}</td>
                  <td className="text-success">{item.stockIn}</td>
                  <td className="">{item.stockOut}</td>
                  <td>{item.stockIn - item.stockOut}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </Template>
  );
}
