import { useEffect, useState } from "react";
import Template from "../components/Template";
import swal from "sweetalert2";
import axios from "axios";
import config from "../config";

import "../css/sale.css"; // Make sure to import the CSS file
import BillSaleDetail from "../../../../api/models/BillSaleDetailModel";

export default function Sale() {
  const [products, setProducts] = useState([]);
  const [billSale, setBillSale] = useState({});
  const [currentBill, setCurrentBill] = useState({});
  const [totalPrice, setTotalPrice] = useState(0); // Add state for total price

  useEffect(() => {
    fetchData();
    openBill();
    fetchBillSaleDetail();
  }, []);

  const fetchBillSaleDetail = async () => {
    try {
      const res = await axios.get(
        `${config.api_path}/billSale/currentBillinfo`,
        config.headers()
      );
      setCurrentBill(res.data.result);
      calculateTotalPrice(res.data.result);
    } catch (e) {
      showErrorAlert(e.message);
    }
  };

  const openBill = async () => {
    try {
      const res = await axios.get(
        `${config.api_path}/billSale/openBill`,
        config.headers()
      );
      setBillSale(res.data.result);
    } catch (e) {
      showErrorAlert(e.message);
    }
  };

  const fetchData = async () => {
    try {
      const res = await axios.get(
        `${config.api_path}/product/listForSale`,
        config.headers()
      );
      if (res.data.message === "success") {
        setProducts(res.data.result);
      }
    } catch (e) {
      showErrorAlert(e.message);
    }
  };

  const Sale = async (item) => {
    try {
      const res = await axios.post(
        `${config.api_path}/billSale/sale`,
        item,
        config.headers()
      );
      if (res.data.message === "success") {
        fetchBillSaleDetail();
      }
    } catch (e) {
      showErrorAlert(e.message);
    }
  };

  const showErrorAlert = (message) => {
    swal.fire({
      title: "Error",
      text: message,
      icon: "error",
    });
  };

  const calculateTotalPrice = (billSaleDetails) => {
    let sum = 0;
    if (billSaleDetails && billSaleDetails.length > 0) {
      for (let i = 0; i < billSaleDetails.length; i++) {
        const item = billSaleDetails[i];
        const qty = parseInt(item.qty);
        const price = parseInt(item.price);

        sum += qty * price;
      }
    }
    setTotalPrice(sum); // Set the calculated total price
  };

  return (
    <Template>
      <div className="card mt-4">
        <div className="card-header d-flex justify-content-between align-items-center bg-success text-white">
          <div className="h5 mb-0">ขายสินค้า</div>
          <div>
            <button className="btn btn-light me-2">
              <i className="fa fa-check me-2"></i>จบการขาย
            </button>
            <button className="btn btn-info me-2">
              <i className="fa fa-file me-2"></i>จบบิลวันนี้
            </button>
            <button className="btn btn-secondary">
              <i className="fa fa-file-alt me-2"></i>จบบิลล่าสุด
            </button>
          </div>
        </div>
        <div className="card-body">
          <div className="row mt-3">
            <div className="col-9">
              <div className="row">
                {products.length > 0 ? (
                  products.map((item) => (
                    <div
                      className="col-12 col-sm-6 col-md-4 col-lg-3 mb-4"
                      key={item.id}
                    >
                      <div className="card h-100 card-hover">
                        <img
                          className="card-img-top product-img"
                          src={`${config.api_path}/uploads/${item.productImages[0].imageName}`}
                          alt={item.name}
                        />
                        <div className="card-body">
                          <h5 className="card-title text-primary text-center">
                            {item.name}
                          </h5>
                          <p className="card-text">
                            {parseInt(item.price).toLocaleString("th-TH")}
                            &nbsp;฿
                          </p>
                          <button
                            className="btn btn-primary"
                            onClick={() => Sale(item)}
                          >
                            <i className="fa fa-shopping-cart"></i> ซื้อ
                          </button>
                        </div>
                      </div>
                    </div>
                  ))
                ) : (
                  <p className="text-center">No products available</p>
                )}
              </div>
            </div>

            <div className="col-3 d-flex flex-column align-items-end">
              <div className="text-end mb-4">
                <span className="bg-dark text-white h2 ps-5 pe-5 py-2 rounded">
                  {totalPrice.toLocaleString("th-TH")} ฿
                </span>
              </div>
              {currentBill.billsaledetails && currentBill.billsaledetails.length > 0 ? (
                currentBill.billsaledetails.map((item, index) => (
                  <div
                    key={index}
                    className="list-group-item text-end justify-content-between align-items-center card w-100"
                  >
                    <div className="mt-2 text-primary">{item.product.name}</div>
                    <div className="mt-2 mb-2">
                      <span>
                        {item.qty} x {item.productName}
                      </span>
                      <span>
                        {item.price.toLocaleString("th-TH")} ={" "}
                        {(item.qty * item.price).toLocaleString("th-TH")} ฿
                      </span>
                    </div>
                  </div>
                ))
              ) : (
                <p className="text-center text-danger">
                  ***ยังไม่มีการสั่งซื้อสินค้า***
                </p>
              )}
            </div>
          </div>
        </div>
      </div>
    </Template>
  );
}
