import { useEffect, useState } from "react";
import Template from "../components/Template";
import swal from "sweetalert2";
import axios from "axios";
import config from "../config";

export default function Sale() {
  const [products, setProducts] = useState([]);
  const [billSale, setbillSale] = useState({});
  useEffect(() => {
    fetchData();
    openBill();
  }, []);

  const openBill = async () => {
    try {
      await axios
        .get(config.api_path + "/billSale/openBill", config.headers())
        .then((res) => {
          setbillSale(res.data.result);
        });
    } catch (e) {
      swal.fire({
        title: "Error",
        text: e.message,
        icon: "error",
      });
    }
  };
  const fetchData = async () => {
    try {
      await axios
        .get(config.api_path + "/product/listForSale", config.headers())
        .then((res) => {
          if (res.data.message === "success") {
            setProducts(res.data.result);
          }
        })
        .catch((err) => {
          throw err.response.data;
        });
    } catch (e) {
      swal.fire({
        title: "Error",
        text: e.message,
        icon: "error",
      });
    }
  };
  return (
    <>
      <Template>
        <div className="card">
          <div className="card-header">
            <div>
              <div className="float-start mt-2 h5 border p-2 bg-success rounded">
                ขายสินค้า
              </div>
              <div className="float-end">
                <button className="btn btn-success">
                  <i className="fa fa-check me-2"></i>จบการขาย
                </button>
                <button className="btn btn-info ms-2">
                  <i className="fa fa-file me-2"></i>จบบิลวันนี้
                </button>
                <button className="btn btn-secondary ms-2">
                  <i className="fa fa-file-alt me-2"></i>จบบิลล่าสุด
                </button>
                <div className="float-none"></div>
              </div>
            </div>
          </div>
          <div className="card-body">
            <div className="text-end">
              <span className="bg-dark h2 ps-5 pe-5">0.00</span>
            </div>
            <div className="input-group mt-3">
              <span className="input-group-text">Barcode</span>
              <input className="form-control" />
              <button className="btn btn-primary">
                <i className="fa fa-check"></i>บันทึก
              </button>
            </div>

            <div className="row">
              {products.length > 0
                ? products.map((item) => (
                    <div className="col-3">
                      <div className="card mt-3">
                        <img
                          className="card-img-top"
                          src={config.api_path + "/uploads/" + item.prodimageName}
                        />
                        <div>img</div>
                        <div>{item.name}</div>
                        <div>{item.price}</div>
                      </div>
                    </div>
                  ))
                : ""}
            </div>
          </div>
        </div>
      </Template>
    </>
  );
}
